mapper.select:
SELECT MEMBER_ID
FROM   MEMBER
WHERE  MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET    HP = #{hp}
WHERE  MEMBER_ID = #{memberId}

		UPDATE MEMBER
        SET    ADDRESS_ZIPCODE = #{addressZipcode},
               ADDRESS_ROAD = #{addressRoad},
               ADDRESS_JIBUN = #{addressJibun},
               ADDRESS_NAMUJI = #{addressNamuji}
        WHERE  MEMBER_ID = #{memberId}
